// Receive the variables as parameters
const run = (offset, attendanceTime) => {
    attendanceTime = JSON.parse(attendanceTime);
    offset = parseInt(offset);
    if(attendanceTime.start == 'no_value' && attendanceTime.end == 'no_value') return true;
    let now = nowUTC(offset);
    let startDate = utcDate(attendanceTime.start, now);
    let endDate = utcDate(attendanceTime.end, now);

    const isHoliday = checkHoliday(attendanceTime.holidays, now);
    const hasWorkDay = attendanceTime.days != 'no_value' ? isWorkDay(now, attendanceTime.days) : true;
    return ((now - startDate) > 0) && ((endDate - now) > 0) && hasWorkDay && !isHoliday;
}

//Get now UTC Date
const nowUTC = (offset) => {
    let now = new Date;
    let utc_timestamp = Date.UTC(now.getUTCFullYear(), now.getUTCMonth(), now.getUTCDate(),
        now.getUTCHours(), now.getUTCMinutes(), now.getUTCSeconds(), now.getUTCMilliseconds());

    return new Date(utc_timestamp + offset * 3600 * 1000);
}

//Get UTC Date
const utcDate = (timeString, today) => {
    let now = new Date;

    let hour = getValueByString('hour', timeString);
    let minutes = getValueByString('minutes', timeString);
    let utc_timestamp = Date.UTC(today.getFullYear(), today.getMonth(), today.getDate(),
        hour, minutes, 0, 0);
    return new Date(utc_timestamp);
}

//Get hour/minute by string with pattern HH:mm
const getValueByString = (type, timeString) => {

    if (type === 'hour') {
        return parseInt(timeString.substring(0, timeString.indexOf(':')));
    }

    else if (type === 'minutes') {
        return parseInt(timeString.substring(timeString.indexOf(':') + 1, timeString.length));
    }

    return 0;
}

//Get if today is a work day
const isWorkDay = (today, workDays) => {
    workDays = workDays.split(',');

    return workDays.indexOf(today.getDay().toString()) >= 0;
}

const checkHoliday = (holidays, now) => {
    let month = now.getMonth() < 10? `0${now.getMonth() + 1}`: now.getMonth() + 1;
    let day = now.getDate() < 10 ? `0${now.getDate()}`: now.getDate(); 
    const today = `${day}/${month}/${now.getFullYear()}`;
    return holidays.some(h => h == today);
}

