const run = (result, contactQueue) => {
    result = JSON.parse(result);
    contactQueue = JSON.parse(contactQueue);

    let hasAttendant = false;
    if (contactQueue.hasKey && result.resource && result.resource.total >= 1) {
        hasAttendant = result.resource.items.some(function (attendant) {
            return attendant.status === 'Online' && (attendant.teams.indexOf(contactQueue.value) >= 0);
        });
    }
    else if (result.resource && result.resource.total >= 1) {
        hasAttendant = result.resource.items.some(function (attendant) {
            return attendant.status === 'Online';
        });
    }
    return hasAttendant;
}

const run = (score) => {
    if(isNumeric(score)){
       score = parseInt(score);
       if(score < 0) return 0;
       if(score > 10) return 10;
       return score;
    }    
    return parseTextScoreToNumber(score);
}

//check if a string has a number
const isNumeric = (num) => {
  return !isNaN(num);
}

const verifyTextScore = (score) => {
    let expression = /^(?:zero|um|dois|tres|quatro|cinco|seis|sete|oito|nove|dez)$/;  
    const result = score.match(expression);
    return result ? result[0] : "Input inesperado";
}

const parseTextScoreToNumber = (score) => {
    const textScore = verifyTextScore(score);
    switch (textScore) {
        case 'zero':
            return 0;
        case 'um':
            return 1;
        case 'dois':
            return 2;
        case 'tres':
            return 3;
        case 'quatro':
            return 4;
        case 'cinco':
            return 5;
        case 'seis':
            return 6;
        case 'sete':
            return 7;
        case 'oito':
            return 8;
        case 'nove':
            return 9;
        case 'dez':
            return 10;
        default:
            return textScore;
  }
}

