/**
            * All input variables needs to be passed as function param;
            * Objects received as param needs to be parsed. Ex.: JSON.parse(inputVariable1);
            * Objects returned needs to be stringfied. Ex.: JSON.stringify(inputVariable1);
            **/

const getValue = (resourceResponse) => {
    resourceResponse = JSON.parse(resourceResponse);
    return resourceResponse.status == 'success' ? resourceResponse.resource : "no_value";
}


const run = (starTime, endTime, days, holidays) => {
    return {
        start: getValue(starTime),
        end: getValue(endTime),
        days: getValue(days),
        holidays: getValue(holidays).split(';')
    };
}

const get_teams = (attendanceRules) => {
    if (attendanceRules.length == 0) return "no_rules";
    const teams = Array.from(new Set(attendanceRules.map(ar => ar.team.replace(/\s/g, ""))));
    if(teams != undefined){
        const teamRegex = teams.reduce((tr, teamsRegex) => `${tr}|${teamsRegex.trim()}`);
        return teamRegex;
    }

    return "no_rules";
}


const run = (contact, attendanceRulesResponse) =>{
    contact = JSON.parse(contact);
    attendanceRulesResponse = JSON.parse(attendanceRulesResponse);
    const attendanceRules = attendanceRulesResponse.status == 'success' ? attendanceRulesResponse.resource.items : [];
    const queuePattern = new RegExp("queue|fila|equipe|team|time|atendente|attendant", "i");
    const queueKey = contact.extras ? Object.keys(contact.extras).find(c => queuePattern.test(c)) : undefined;
    const hasKey = queueKey != undefined;
    return {
        hasKey : hasKey,
        value : hasKey ? contact.extras[queueKey] : "",
        available: get_teams(attendanceRules)
    };
}

const default_start = /inicio|opening|abertura/i;
const default_end = /fim|final|closing|fechamento|closure/i;
const default_work_days = /dias|days/i;
const desk_res = /desk/i;
const holiday_pattern = /holyday|feriado|folga|rest/i
const days = [
    /sunday|domingo/i,
    /monday|segunda|lunes/i,
    /tuesday|ter[Ã§|c]a|martes/i,
    /wednesday|quarta|mi(Ã©|e)rcoles/i,
    /jueves|thursday|quinta/i,
    /friday|sexta|viernes/i,
    /s(Ã¡|a)bado|saturday/i,
];

const getVariableNames = (desk_resources, day, queue, needle) => {
    const teamsAvailablePattern = new RegExp(queue.available, "i");
    const res_day_queue = desk_resources.find(
        (dr) =>
            queue.hasKey &&
            dr.toLowerCase().includes(queue.value.toLowerCase()) &&
            day.test(dr) &&
            needle.test(dr)
    );
    if (res_day_queue) return res_day_queue;
    const res_queue = desk_resources.find(
        (dr) =>
            !days.some((d) => d.test(dr)) &&
            queue.hasKey &&
            dr.toLowerCase().includes(queue.value.toLowerCase()) &&
            needle.test(dr)
    );
    if (res_queue) return res_queue;
    const res_day = desk_resources.find(
        (dr) => 
            day.test(dr) && 
            !teamsAvailablePattern.test(dr) && 
            needle.test(dr)
    );
    if (res_day) return res_day;
    const res_needle = desk_resources.find(
        (dr) =>
            !days.some((d) => d.test(dr)) &&
            !teamsAvailablePattern.test(dr) &&
            needle.test(dr)
    );
    if (res_needle) return res_needle;
    return 'no_resource';
};

const getStartAndEndVariableNames = (desk_resources, day, queue) => {
    const start_name = getVariableNames(
        desk_resources,
        day,
        queue,
        default_start
    );
    const end_name = getVariableNames(desk_resources, day, queue, default_end);
    const days = day.test(start_name) || day.test(end_name) ? 'no_resource' : getVariableNames(
        desk_resources,
        day,
        queue,
        default_work_days
    );
    const holidays = getVariableNames(desk_resources, day, queue, holiday_pattern);
    return {
        start: start_name,
        end: end_name,
        days,
        holidays: holidays
    };
};

const getTodayDate = (offset) => {
    const now = new Date();
    const utc_timestamp = Date.UTC(
        now.getUTCFullYear(),
        now.getUTCMonth(),
        now.getUTCDate(),
        now.getUTCHours(),
        now.getUTCMinutes(),
        now.getUTCSeconds(),
        now.getUTCMilliseconds()
    );

    return new Date(utc_timestamp + offset * 3600 * 1000).getDay();
};

const getDeskResource = (items) => items.filter((i) => desk_res.test(i)) || items;

const run = (resources, offset, queue) => {
    queue = JSON.parse(queue);
    resources = JSON.parse(resources);

    offset = offset ? offset : 0;
    if (resources.status != 'success') {
        return {
            start: 'no_resource',
            end: 'no_resource',
            days: 'no_resource',
            holidays: 'no_resource'
        };

    }
    const desk_resources = getDeskResource(resources.resource.items);

    const day = days[getTodayDate(offset)];
    return getStartAndEndVariableNames(desk_resources, day, queue);
};
